# Частотный анализ букв в тексте
def count_letters(text):
    text = text.lower()
    counted = {}
    for alpha in text:
        if alpha.isalpha():
            counter = 0
            for char in text:
                if char == alpha:
                    counter += 1
            counted.setdefault(alpha, counter)
    return counted

def calculate_frequency(frecuencies):
    counter = sum(frecuencies.values())
    for key in frecuencies:
        frecuency = frecuencies[key] / counter
        frecuency = round(frecuency, 2)
        frecuencies[key] = frecuency
    return frecuencies

#My text
text = "Умей прощать, и мощь твоя умножится. (Публий Сир)"

# Вывод кол-ва
print(text,'\n')
print("Количество букв:")
for key, value in count_letters(text).items():
    print(f'{key}:', value)

# Вывод частоты
print("\nЧастота появления в тексте:")
frecuencies = calculate_frequency(count_letters(text))
for key, value in frecuencies.items():
    print(f'{key}:', value)
